Ext.define('JWF.controller.Runs', {
    extend: 'Ext.app.Controller',

    config: {
        control: {
            "#addRunButton": {
                tap: 'addRun'
            },
            "#showFormButton": {
                tap: 'showForm'
            },
            "#addRunBackBtn": {
                tap: 'hideForm'
            },
            "#showTOP": {
                tap: 'showTOP'
            },
            "#showPROFILE": {
                tap: 'onButtonTap'
            },
            "#home": {
                tap: 'onButtonTap1'
            }
        }
    },

    addRun: function(button, e, options) {
        var distance = Ext.getCmp('distanceField').getValue(),
            location = 'STEINVORTH',
            caption = JWF.userData.first_name + ' esta en este momento en ' + location ;


        Ext.getCmp('runForm').setMasked({
            xtype: 'loadmask',
            message: 'Agregando tweet..'
        });

        Ext.Ajax.request({
            url: '/run',
            method: 'POST',
            params: {
                location: location,
                distance: caption
            },
            callback: this.onAddRun,
            scope: this
        });
    },

    showForm: function(button, e, options) {
        if (!this.addRunForm) {
            this.addRunForm = Ext.create('JWF.view.Form', {
                id: 'runForm'
            });
        }
        Ext.Viewport.setActiveItem(this.addRunForm);
    },

    hideForm: function(button, e, options) {
        Ext.Viewport.setActiveItem(Ext.getCmp('main'));
        Ext.getCmp('runForm').hide();
    },

    showTOP: function(button, e, options) {
        if (!this.showTOP) {
            this.showTOP = Ext.create('JWF.view.MyPanel1', {
                id: 'runTOP'
            });
        }
        var main = Ext.getCmp('main'),
            runList = Ext.getCmp('runList'),
            noFriends = Ext.getCmp('noFriends');

        main.setActiveItem( this.showTOP);


    },

    onButtonTap: function(button, e, options) {
        if (!this.showPROFILE) {
            this.showPROFILE = Ext.create('JWF.view.MyPanel2', {
                id: 'runprofile'
            });
        }
        var main = Ext.getCmp('main'),
            runList = Ext.getCmp('runList'),
            noFriends = Ext.getCmp('noFriends');

        main.setActiveItem( this.showPROFILE);
        Ext.getCmp('runForm').hide();
        Ext.getCmp('runTOP').hide();
    },

    onButtonTap1: function(button, e, options) {
        var main = Ext.getCmp('main'),
            runList = Ext.getCmp('runList'),
            noFriends = Ext.getCmp('noFriends');


        if (!runList) {
            runList = Ext.create('JWF.view.run.List', {
                id: 'runList'
            });
        }
        main.setActiveItem(runList);
    },

    init: function(application) {
        this.callParent();

        Ext.getStore('Runs').on('load', this.onRunsLoad);

    },

    onRunsLoad: function(store) {
        var main = Ext.getCmp('main'),
            runList = Ext.getCmp('runList'),
            noFriends = Ext.getCmp('noFriends');

        if (store.getCount()) {
            if (!runList) {
                runList = Ext.create('JWF.view.run.List', {
                    id: 'runList'
                });
            }
            main.setActiveItem(runList);
        } else {
            if (!noFriends) {
                noFriends = Ext.create('JWF.view.NoFriends', {
                    id: 'noFriends',
                    data: JWF.userData
                });
            }
            main.setActiveItem(noFriends);
        }
    },

    onAddRun: function(options, success, response) {
        Ext.getCmp('runForm').setMasked(false);
        this.hideForm();
        Ext.getStore('Runs').load();
    }

});